struct Uniforms {
    time: f32,
};

@group(0) @binding(0) var<uniform> uniforms: Uniforms;

@fragment
fn main(@builtin(position) coord: vec4<f32>) -> @location(0) vec4<f32> {
    let uv = coord.xy / 800.0;
    let r = sin(uniforms.time + uv.x * 3.14159) * 0.5 + 0.5;
    let g = cos(uniforms.time + uv.y * 3.14159) * 0.5 + 0.5;
    let b = sin(uniforms.time * 2.0) * 0.5 + 0.5;
    return vec4<f32>(r, g, b, 1.0);
}

struct Material {
    albedo: vec3<f32>,
    metallic: f32,
    roughness: f32,
};

struct Light {
    position: vec3<f32>,
    color: vec3<f32>,
};

@group(0) @binding(0) var<uniform> material: Material;
@group(0) @binding(1) var<uniform> light: Light;

fn distributionGGX(N: vec3<f32>, H: vec3<f32>, roughness: f32) -> f32 {
    let a = roughness * roughness;
    let a2 = a * a;
    let NdotH = max(dot(N, H), 0.0);
    let NdotH2 = NdotH * NdotH;

    let num = a2;
    let denom = (NdotH2 * (a2 - 1.0) + 1.0);
    let denom2 = 3.14159265359 * denom * denom;

    return num / denom2;
}

@fragment
fn main(@location(0) world_pos: vec3<f32>, @location(1) normal: vec3<f32>) -> @location(0) vec4<f32> {
    let N = normalize(normal);
    let V = normalize(-world_pos);
    let L = normalize(light.position - world_pos);
    let H = normalize(V + L);

    let NDF = distributionGGX(N, H, material.roughness);
    let radiance = light.color;

    let NdotL = max(dot(N, L), 0.0);
    let color = material.albedo * radiance * NdotL * NDF;

    return vec4<f32>(color, 1.0);
}

@fragment
fn main(@builtin(position) fragCoord: vec4<f32>) -> @location(0) vec4<f32> {
    let uv = fragCoord.xy / vec2<f32>(512.0, 512.0);
    return vec4<f32>(uv, 0.5, 1.0);
}

